.single-post{
  display: flex;
  flex-direction: column;
  width:100%;
  margin: 20px;
}
.main-content{
  width:60%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin:0px auto;
  align-content: center;
}

.post-title {
  width:100%;
  display: flex;
  flex-direction: column;;
  font-size: 30px;
  line-height: 10px;
  padding: 2em 0;
  font-family: 'Stylish';
  justify-content: center;
  align-items: center;
}
.post-date {
  width:100%;
  display: flex;
  flex-direction: row;
  font-size: 16px;
  font-weight: 400;
  justify-content: center;
}
.post-content {
  width:100%;
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  font-size: 1rem;
  background-color:whitesmoke;
  margin: 0px auto;
  justify-content: center;
  padding: 10px;
}
