result:
.content{
  display: flex;
  flex-direction:row;
  width:100%;
}
.left-content{
  display: flex;
  flex-direction: column;
  width:100%;
  background-color:white;
  margin-left: 70px;
}
.right-content{
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  flex-wrap: wrap;
  padding:50px;
  width:50%;
  background-color: rgba(100, 98, 98, 0.041);
}
.r-item{
  width:100%;
  margin: 10px;
  color: black;
  font-size: 1rem;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
}
.c-title{
  font-size: 1.5rem;
  color:black;
  font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
  font-weight: 100;
  justify-content: flex-start;
  margin-top:15%;

}
.c-text{
  width:100%;
  font: 1rem;
  color:black;
  border-bottom-style: solid;
  border-color:gray;
  border-width: thin;
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  text-decoration: none;
  margin: 0px;
}
.row-title{
  width: 100%;
  font: 0.8rem;
  padding:10px;

}
@media screen and (max-width: 1200px) {
  .content{
    display: flex;
    flex-direction: column;
    flex-wrap: wrap;
    width:100%;
    justify-content: center;
    align-items: center;
  }
  .left-content{
    display: flex;
    flex-direction: column;
    flex-wrap: nowrap;
    align-self: center;
    margin-left: 10px;
  }
  .right-content{
    display: flex;
    flex-direction: column;
    flex-wrap: nowrap;
    width:100%;
    background-color: rgba(100, 98, 98, 0.041);

  }
}
