import Fixedsidebar from "../layouts/Fixedsidebar";
import PostList from '@/components/PostList';
import SearchBox from '@/components/SearchBox';
import NewestCard from '@/components/NewestCard';
export default {
  metaInfo: {
    title: "Home-Page"
  },
  components: {
    PostList: PostList,
    Fixedsidebar: Fixedsidebar,
    SearchBox: SearchBox,
    NewestCard: NewestCard
  },
  data: function data() {
    return {
      search: ''
    };
  },
  computed: {
    filteredData: function filteredData() {
      var _this = this;

      return this.$page.posts.edges.filter(function (edge) {
        var lowercase_title = edge.node.title.toLowerCase(); //console.log(lowercase_title)
        //console.log(lowercase_title.indexOf(this.search.toLowerCase()))

        return lowercase_title.indexOf(_this.search.toLowerCase()) >= 0;
      });
    }
  },
  methods: {
    selfsearchupdate: function selfsearchupdate(val) {
      this.search = val;
    }
  }
};
