.header {
  font-family: "Stylish";
  font-size: 35px;
  text-align: center;
  line-height: 20px;
  padding: 0.7em;
  margin: 0px auto;
}
.header h2 {
  font-weight: 200;
  font-size: 35px;
}
 /* Profile Content */
  .profile-content {
    width:80%;
    display: flex;
    flex-direction:column;
    padding: 20px;
    background:white;
    flex-wrap: wrap;
    margin :10px 10px;
    flex:1;
  }
  /* content card*/
  .card{
    max-width:25%;
    display:flex;
    flex-direction: column;
    background-color:whitesmoke;
    margin: 5px 10px;
    flex:1;

  }
  @media screen and (max-width:500px)
  {
    .card{
      max-width:50%;
      display:flex;
      flex-direction: column;
      background-color:whitesmoke;
      margin-left: 10px;
      margin-bottom: 20px;
    }
  }
  @media screen and (max-width:370px)
  {
    .card{
      max-width:80%;
      display:flex;
      flex-direction: column;
      background-color:whitesmoke;
      margin-left: 10px;
      margin-bottom: 20px;
      flex-wrap: wrap;
    }
  }
  .card-img-top{
    width: 100%;
    border-style: ridge;
    margin-bottom: 10px;
  }
  .card-body{
    width :100%;
    display: flex;
    flex-direction: column;
    flex-wrap: wrap;
    margin-top:10px;
    margin: 0px auto;
  }
  .card-title{
    text-align: center;
  }
  .card-text{
    text-align: justify;
    margin : 0 5px;
  }
  .btn-primary{
    width: 95%;
    justify-content: center;
    background-color: grey;
    font-size:10 vw;
    display: flex;
    flex-wrap: wrap;
    margin-top: 20%;
    margin-left:2.5%;
  }
