/* background picture */

.bg-1{
  background-image: url('../../content/posts/image/picture-1.png')
}
.bg-2{
  background-image: url('../../content/posts/image/picture-2.png')
}
.bg-3{
  background-image: url('../../content/posts/image/picture-3.png')
}
.bg-4{
  background-image: url('../../content/posts/image/picture-4.png')
}




/* main content*/
.photo-main-content{
  display: flex;
  flex-direction: column;
  min-height: 100%;
  min-width: 100%;
  background-size:contain;
  background-attachment:fixed;
  background-repeat: repeat;
  background-image: url('../../content/posts/image/picture-6.jpg');
}
.item{
  display: flex;
  flex-wrap: wrap;
  width:100%;
}

/* silder content */

.silder-container{
  display: flex;
  flex-direction: column;
  width: 100%;
  background:url('../../content/posts/image/picture-6.jpg') center center repeat;
  background-size: cover;
}
.right-nav{
  justify-content: flex-end;
}
.silder-center{
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  max-width:40%;
  max-height: 40%;
  margin: 15% auto;
}
.silder-text{
  display: flex;
  flex-wrap: nowrap;
  justify-content: center;
  font-family:'Lucida Sans', 'Lucida Sans Regular', 'Lucida Grande', 'Lucida Sans Unicode', Geneva, Verdana, sans-serif;
}
.silder-button{
  display: flex;
  justify-content: center;
  margin: 20% auto;
  color: white;
  width: 100%;
  height: 50%;
  min-height: 50px;
  border-radius: 10px;
  font-size: auto;
  text-decoration-line: none;
  background-color: rgb(157, 207, 240);
}

#silderbtn-text{
  align-items: center;
}
.silder-button:hover{
  background-color: rgba(157, 207, 240, 0.7);
}
.s-text{
  display: flex;
  flex-direction: column;
  flex-wrap: nowrap;
  width: 100%;
}
.small-text
{
  font-size: auto;
  color: #ffffff;
  margin: 0px auto;

}
.large-text{
  font-size:4vw;
  color: #ffffff;
  margin: 0px auto;
}
.text-container{
  flex-direction: column;
  align-items: center;
  width: 100%;
  background-color:#ffffff;
}
.text-container-box{
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  width:70%;
  max-width: 70%;
  justify-content: center;
  align-items: center;
  font-size:1.5vh;
  min-height: 320px;
}
.text-container-aword{
  color: gray;
}
.card-container{
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  width:100%;
  max-width: 100%;
  background-color:rgb(245, 243, 219);
  flex-wrap: wrap;
  padding: 0.5%;
  justify-content: center;
}
.card-title{
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 5vh;
  font-family: 'Lucida Sans', 'Lucida Sans Regular', 'Lucida Grande', 'Lucida Sans Unicode', Geneva, Verdana, sans-serif;
  min-height: 150px;
  max-width: 100%;
}
.card-rol{
  width: 100%;
  padding:1%;
}
.card-rol1-content{
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  max-width: 100%;
  padding:1%;
}
.card-rol2-content{
  width:100%;
}
.card-rol3-content{
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  width:100%;
  margin-top:8%;
}
.rol3-content-text{
  color: gray;
}
.rol3-btn{
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top:1%;
  margin-bottom: 8%;
  width:auto;
  height: 15px;
  min-width: 20%;
  border-radius: 10px;
  font-size: 2vh;
  border-style: solid;
  border-width: 1px;
  padding: 1.5%;

}

.picture-container{
  display: flex;
  flex-direction: column;
  height: auto;
  min-height: 1000px;
  background-color:white;
}
.picture-rol{
  width:100%;
  display: flex;
  padding:1%;
}
.p-rol1{
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  background-color: white;
  width: 100%;
  padding-bottom: 8%;
}
.p-rol2{
  width:100%;
  display: flex;
  flex-wrap: wrap;
  max-width: 100%;
}

.picture-title{
  display: flex;
  width:100%;
  justify-content: center;
  align-items: center;
  font-size: 4rem;
  font-family: 'Trebuchet MS', 'Lucida Sans Unicode', 'Lucida Grande', 'Lucida Sans', Arial, sans-serif;
  margin-bottom: 2%;
  font-weight: 200;
}
.picture-tag{
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  font-size: 3vh;
  font-family: 'Trebuchet MS', 'Lucida Sans Unicode', 'Lucida Grande', 'Lucida Sans', Arial, sans-serif;
  text-decoration-line:underline;
  width: 100%;
}
.tag-text{
  margin-right: 1%;
  font-size: 1.2rem;
  color: black;
  font-weight: 200

}

/* icon-rol content*/

.icon-rol{
  display: flex;
  flex-direction: row;
  height: auto;
}

.review{
  display: flex;
  width: 100%;
  min-height: 400px;
  justify-content: center;
  align-items: center;
}
.review-text{
  width:100%;
  padding: 2% 1%;

}
/* footer content*/
.footer{
  display: flex;
  flex-direction: row;
  min-height: 200px;
  justify-content:center;
  align-items: center;
  background-color: antiquewhite;

}
.footer-img{
  object-fit: cover;
  width:5%;
  height: 5%;
  max-width:10%;
  max-height:10%;
  margin-right: 1%;
}
.text-nav-link{
  font-size: 1.5vh;
  font-weight: 100;
  font-family:'Times New Roman', Times, serif;
  margin-right: 4%;
  color: rgba(0, 0, 0, 0.781);
  text-decoration-line: none;
}

